import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Alert, ScrollView } from 'react-native';

export default function RequestForm() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState('');
  const [notes, setNotes] = useState('');

  function submit() {
    // На этом этапе можно добавить отправку на сервер.
    const payload = { name, phone, location, notes, createdAt: new Date().toISOString() };
    Alert.alert('Заявка отправлена', JSON.stringify(payload, null, 2));
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.label}>Ваше имя</Text>
      <TextInput style={styles.input} value={name} onChangeText={setName} placeholder="Имя" />

      <Text style={styles.label}>Телефон</Text>
      <TextInput style={styles.input} value={phone} onChangeText={setPhone} placeholder="Номер телефона" keyboardType="phone-pad" />

      <Text style={styles.label}>Место (адрес или координаты)</Text>
      <TextInput style={styles.input} value={location} onChangeText={setLocation} placeholder="Где вы находитесь" />

      <Text style={styles.label}>Примечания</Text>
      <TextInput style={[styles.input, { height: 100 }]} value={notes} onChangeText={setNotes} placeholder="Дополнительная информация" multiline />

      <Button title="Отправить заявку" onPress={submit} />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { padding: 16 },
  label: { marginTop: 12, marginBottom: 6, fontWeight: '600' },
  input: { borderWidth: 1, borderColor: '#ccc', padding: 8, borderRadius: 6 }
});
